import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TextInput,
  TouchableOpacity,
  ScrollView,
  Alert,
} from 'react-native';
import { router } from 'expo-router';
import { StatusBar } from 'expo-status-bar';
import { useAuthStore } from '@/store/authStore';

const INTERESTS = [
  'Music', 'Gaming', 'Movies', 'Sports', 'Art', 'Travel',
  'Food', 'Books', 'Technology', 'Photography', 'Dance',
  'Fitness', 'Fashion', 'Comedy', 'Nature', 'Cooking',
];

const PRONOUNS = [
  'he/him', 'she/her', 'they/them', 'other'
];

export default function ProfileSetupScreen() {
  const [displayName, setDisplayName] = useState('');
  const [bio, setBio] = useState('');
  const [selectedInterests, setSelectedInterests] = useState<string[]>([]);
  const [selectedPronouns, setSelectedPronouns] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const { updateProfile } = useAuthStore();

  const toggleInterest = (interest: string) => {
    if (selectedInterests.includes(interest)) {
      setSelectedInterests(prev => prev.filter(i => i !== interest));
    } else if (selectedInterests.length < 3) {
      setSelectedInterests(prev => [...prev, interest]);
    }
  };

  const handleComplete = async () => {
    if (!displayName.trim()) {
      Alert.alert('Error', 'Please enter a display name');
      return;
    }

    if (selectedInterests.length === 0) {
      Alert.alert('Error', 'Please select at least one interest');
      return;
    }

    setIsLoading(true);

    try {
      await updateProfile({
        display_name: displayName.trim(),
        bio: bio.trim(),
        interests: selectedInterests,
        pronouns: selectedPronouns,
      });

      router.replace('/(tabs)');
    } catch (error: any) {
      Alert.alert('Error', error.message || 'Failed to update profile');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <View style={styles.container}>
      <StatusBar style="dark" />
      <ScrollView contentContainerStyle={styles.scrollContent}>
        <View style={styles.header}>
          <Text style={styles.title}>Complete Your Profile</Text>
          <Text style={styles.subtitle}>Tell us about yourself</Text>
        </View>

        <View style={styles.form}>
          <View style={styles.inputContainer}>
            <Text style={styles.label}>Display Name *</Text>
            <TextInput
              style={styles.input}
              value={displayName}
              onChangeText={setDisplayName}
              placeholder="How should we call you?"
              placeholderTextColor="#999"
              maxLength={50}
            />
          </View>

          <View style={styles.inputContainer}>
            <Text style={styles.label}>Bio</Text>
            <TextInput
              style={[styles.input, styles.textArea]}
              value={bio}
              onChangeText={setBio}
              placeholder="Tell us something interesting about yourself..."
              placeholderTextColor="#999"
              multiline
              numberOfLines={3}
              maxLength={150}
            />
            <Text style={styles.characterCount}>{bio.length}/150</Text>
          </View>

          <View style={styles.inputContainer}>
            <Text style={styles.label}>Pronouns</Text>
            <View style={styles.pronounsContainer}>
              {PRONOUNS.map((pronoun) => (
                <TouchableOpacity
                  key={pronoun}
                  style={[
                    styles.pronounChip,
                    selectedPronouns === pronoun && styles.selectedPronounChip,
                  ]}
                  onPress={() => setSelectedPronouns(pronoun)}
                >
                  <Text
                    style={[
                      styles.pronounText,
                      selectedPronouns === pronoun && styles.selectedPronounText,
                    ]}
                  >
                    {pronoun}
                  </Text>
                </TouchableOpacity>
              ))}
            </View>
          </View>

          <View style={styles.inputContainer}>
            <Text style={styles.label}>Interests * (Select up to 3)</Text>
            <View style={styles.interestsContainer}>
              {INTERESTS.map((interest) => (
                <TouchableOpacity
                  key={interest}
                  style={[
                    styles.interestChip,
                    selectedInterests.includes(interest) && styles.selectedChip,
                  ]}
                  onPress={() => toggleInterest(interest)}
                >
                  <Text
                    style={[
                      styles.interestText,
                      selectedInterests.includes(interest) && styles.selectedText,
                    ]}
                  >
                    {interest}
                  </Text>
                </TouchableOpacity>
              ))}
            </View>
            <Text style={styles.selectedCount}>
              {selectedInterests.length}/3 selected
            </Text>
          </View>

          <TouchableOpacity
            style={[styles.completeButton, isLoading && styles.disabledButton]}
            onPress={handleComplete}
            disabled={isLoading}
          >
            <Text style={styles.completeButtonText}>
              {isLoading ? 'Saving...' : 'Complete Profile'}
            </Text>
          </TouchableOpacity>
        </View>
      </ScrollView>
    </View>
  );
}

