import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  FlatList,
  TouchableOpacity,
  RefreshControl,
} from 'react-native';
import { router } from 'expo-router';
import { StatusBar } from 'expo-status-bar';
import { MessageCircle, Users } from 'lucide-react-native';
import { useAuthStore } from '@/store/authStore';
import { supabase, Conversation, User, Message } from '@/lib/supabase';

interface ConversationItem extends Conversation {
  other_user: User;
  last_message: Message | null;
  unread_count: number;
}

export default function ChatsScreen() {
  const [conversations, setConversations] = useState<ConversationItem[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isRefreshing, setIsRefreshing] = useState(false);

  const { user } = useAuthStore();

  useEffect(() => {
    if (user) {
      loadConversations();

      // Set up real-time subscription for new messages
      const channel = supabase
        .channel('conversations')
        .on('postgres_changes', {
          event: '*',
          schema: 'public',
          table: 'messages',
        }, () => {
          loadConversations();
        })
        .subscribe();

      return () => {
        supabase.removeChannel(channel);
      };
    }
  }, [user]);

  const loadConversations = async (refresh = false) => {
    if (!user) return;

    if (!refresh) setIsLoading(true);
    if (refresh) setIsRefreshing(true);

    try {
      const { data: conversationsData, error } = await supabase
        .from('conversations')
        .select(`
          *,
          user_a:users!conversations_user_a_id_fkey(*),
          user_b:users!conversations_user_b_id_fkey(*)
        `)
        .or(`user_a_id.eq.${user.id},user_b_id.eq.${user.id}`)
        .order('last_message_at', { ascending: false });

      if (error) throw error;

      // Get last message and unread count for each conversation
      const conversationItems: ConversationItem[] = await Promise.all(
        (conversationsData || []).map(async (conv) => {
          // Get other user
          const otherUser = conv.user_a_id === user.id ? conv.user_b : conv.user_a;

          // Get last message
          const { data: lastMessage } = await supabase
            .from('messages')
            .select('*')
            .eq('conversation_id', conv.id)
            .order('created_at', { ascending: false })
            .limit(1)
            .single();

          // Get unread count
          const { count: unreadCount } = await supabase
            .from('messages')
            .select('*', { count: 'exact', head: true })
            .eq('conversation_id', conv.id)
            .eq('is_read', false)
            .neq('sender_id', user.id);

          return {
            ...conv,
            other_user: otherUser,
            last_message: lastMessage,
            unread_count: unreadCount || 0,
          };
        })
      );

      setConversations(conversationItems);
    } catch (error) {
      console.error('Error loading conversations:', error);
    } finally {
      setIsLoading(false);
      setIsRefreshing(false);
    }
  };

  const formatMessagePreview = (message: Message | null): string => {
    if (!message) return 'No messages yet';

    if (message.message_type === 'text') {
      return message.content.length > 50 
        ? `${message.content.substring(0, 50)}...`
        : message.content;
    }

    return 'ðŸ“· Photo';
  };

  const formatLastMessageTime = (date: string): string => {
    const now = new Date();
    const messageDate = new Date(date);
    const diffMs = now.getTime() - messageDate.getTime();
    const diffMins = Math.floor(diffMs / 60000);
    const diffHours = Math.floor(diffMs / 3600000);
    const diffDays = Math.floor(diffMs / 86400000);

    if (diffMins < 1) return 'now';
    if (diffMins < 60) return `${diffMins}m`;
    if (diffHours < 24) return `${diffHours}h`;
    if (diffDays < 7) return `${diffDays}d`;

    return messageDate.toLocaleDateString();
  };

  const renderConversationItem = ({ item }: { item: ConversationItem }) => (
    <TouchableOpacity
      style={styles.conversationItem}
      onPress={() => router.push(`/chat/${item.id}`)}
    >
      <View style={styles.avatar}>
        <Text style={styles.avatarText}>
          {item.other_user.display_name.charAt(0).toUpperCase()}
        </Text>
      </View>

      <View style={styles.conversationInfo}>
        <View style={styles.conversationHeader}>
          <Text style={styles.userName}>{item.other_user.display_name}</Text>
          <Text style={styles.timestamp}>
            {item.last_message 
              ? formatLastMessageTime(item.last_message.created_at)
              : formatLastMessageTime(item.created_at)
            }
          </Text>
        </View>

        <View style={styles.messagePreview}>
          <Text style={[
            styles.lastMessage,
            item.unread_count > 0 && styles.unreadMessage
          ]}>
            {formatMessagePreview(item.last_message)}
          </Text>
          {item.unread_count > 0 && (
            <View style={styles.unreadBadge}>
              <Text style={styles.unreadCount}>
                {item.unread_count > 99 ? '99+' : item.unread_count}
              </Text>
            </View>
          )}
        </View>
      </View>
    </TouchableOpacity>
  );

  const renderEmptyState = () => (
    <View style={styles.emptyState}>
      <MessageCircle size={64} color="#cccccc" />
      <Text style={styles.emptyStateTitle}>No conversations yet</Text>
      <Text style={styles.emptyStateText}>
        Make friends through video calls to start chatting!
      </Text>
      <TouchableOpacity
        style={styles.findFriendsButton}
        onPress={() => router.push('/(tabs)')}
      >
        <Users size={20} color="#ffffff" />
        <Text style={styles.findFriendsButtonText}>Start Video Call</Text>
      </TouchableOpacity>
    </View>
  );

  return (
    <View style={styles.container}>
      <StatusBar style="dark" />

      <View style={styles.header}>
        <Text style={styles.title}>Chats</Text>
        <Text style={styles.subtitle}>
          {conversations.length} conversation{conversations.length !== 1 ? 's' : ''}
        </Text>
      </View>

      <FlatList
        data={conversations}
        renderItem={renderConversationItem}
        keyExtractor={(item) => item.id}
        contentContainerStyle={[
          styles.listContent,
          conversations.length === 0 && styles.emptyListContent
        ]}
        ListEmptyComponent={renderEmptyState}
        refreshControl={
          <RefreshControl
            refreshing={isRefreshing}
            onRefresh={() => loadConversations(true)}
            tintColor="#8B5CF6"
          />
        }
        showsVerticalScrollIndicator={false}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#ffffff',
  },
  header: {
    paddingHorizontal: 24,
    paddingTop: 60,
    paddingBottom: 20,
    borderBottomWidth: 1,
    borderBottomColor: '#f1f5f9',
  },
  title: {
    fontSize: 32,
    fontWeight: 'bold',
    color: '#1a1a1a',
    marginBottom: 4,
  },
  subtitle: {
